export const theme = {
  colors: {
    text: '#000',
    background: '#fff',
    primary: 'rgb(231, 231, 231)',
    secondary: '#919191',
    accent: 'orange',
    muted: '#d2d5f0',
  },
  space: [0, 4, 8, 16, 32, 64, 128, 256],
  fonts: {
    body: 'Roboto, sans-serif',
    heading: 'Helvetica Neue, sans-serif',
    monospace: 'Menlo, monospace',
  },
  // Для компонента BOX
  fontSizes: [12, 14, 16, 32, 40, 64],
  // Для всех остальних компонентов
  fontSize: {
    xs: 12,
    s: 14,
    m: 16,
    l: 32,
    xl: 64,
  },
  fontWeights: {
    body: 400,
    heading: 500,
    bold: 700,
  },
  lineHeights: {
    body: 1.5,
    heading: 1.125,
  },
  radii: {
    none: '0',
    normal: '4px',
    round: '50%',
  },
};
